import "./App.css";
import { useState } from "react";
import Editor from "./components/Editor";
function App() {
  const [html, setHtml] = useState('')
  const [css, setCss] = useState('')
  const [javscript, setJavascript] = useState('')

  return (
    <>
      <div className="pane top-pane">
        <Editor
        language="xml"
         displayname="HTML"
         value={html}
         onChange={setHtml} />
                <Editor
        language="css"
         displayname="CSS"
         value={css}
         onChange={setCss} />
               <Editor
        language="javascript"
         displayname="JS"
         value={html}
         onChange={setHtml} />
      </div>

      <div className="pane">
        <iframe title="output" sandbox="allow-scripts"
         border="0" 
         width="100%"
         height="100%"
         />
      </div>
    </>
  );
}

export default App;
