import "./App.css";
import { useState, useEffect } from "react";
import Editor from "./components/Editor";
import useLocalStorage from "./hooks/useLocalStorage";
function App() {
  const [html, setHtml] = useLocalStorage('html',"");
  const [css, setCss] = useLocalStorage("css", '');
  const [javscript, setJavascript] = useLocalStorage("javscript");
  const [srcDoc, setSrcdoc] = useState("");

  useEffect(() => {
    const timeout = setTimeout(() => {
      setSrcdoc(`
  <html>
   <body>${html}</body>
   <style>${css}</style>
   <script>${javscript}</script>
  <html/>
  `);
    }, 250);

    return () => clearTimeout(timeout) 
  }, [html, css, javscript]);

  return (
    <>
      <div className="pane top-pane">
        <Editor
          language="xml"
          displayname="HTML"
          value={html}
          onChange={setHtml}
        />
        <Editor
          language="css"
          displayname="CSS"
          value={css}
          onChange={setCss}
        />
        <Editor
          language="javascript"
          displayname="JS"
          value={javscript}
          onChange={setJavascript}
        />
      </div>

      <div className="pane">
        <iframe
          srcDoc={srcDoc}
          title="output"
          sandbox="allow-scripts"
          border="0"
          width="100%"
          height="100%"
        />
      </div>
    </>
  );
}

export default App;
